import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [message, setMessage] = useState("Nothing happened yet üò¥");
  const [loading, setLoading] = useState(false);

  const recreateDatabase = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:8080/api/init", {
        method: "POST",
      });

      if (!response.ok) throw new Error("Something went wrong");

      const text = await response.text();
      setMessage(`‚úÖ Success: ${text}`);
    } catch (err) {
      setMessage(`‚ùå Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üìö Biblioteca App</h1>
        <button onClick={recreateDatabase} disabled={loading}>
          {loading ? "Recreating..." : "üîÅ Recreate Database"}
        </button>
        <p>{message}</p>
      </header>
    </div>
  );
}

export default App;
