import React, { useState } from 'react';

const ObraForm = () => {
  const [titulo, setTitulo] = useState("");
  const [ano, setAno] = useState("");
  const [idGenero, setIdGenero] = useState("");
  const [idEditora, setIdEditora] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8080/api/obras", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ titulo, ano: parseInt(ano), idGenero, idEditora })
      });

      if (!response.ok) throw new Error("Erro ao cadastrar obra");

      const text = await response.text();
      setMensagem(`Sucesso: ${text}`);
      setTitulo("");
      setAno("");
      setIdGenero("");
      setIdEditora("");
    } catch (err) {
      setMensagem(`Erro: ${err.message}`);
    }
  };

  return (
    <div>
      <h2>Nova Obra</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Título" value={titulo} onChange={e => setTitulo(e.target.value)} required />
        <input placeholder="Ano" type="number" value={ano} onChange={e => setAno(e.target.value)} required />
        <input placeholder="ID Gênero" value={idGenero} onChange={e => setIdGenero(e.target.value)} required />
        <input placeholder="ID Editora" value={idEditora} onChange={e => setIdEditora(e.target.value)} required />
        <button type="submit">➕ Cadastrar Obra</button>
      </form>
      <p>{mensagem}</p>
    </div>
  );
};

export default ObraForm;
